// @flow

import { castArr } from './arr'
import { isStr, isF, isArr } from './is'
import type { AnyMap } from './_types'


type Listener = ( AnyMap ) => void

/**
 * EventEmitter
 *
 * @export
 * @class Events
 */
export default class EventEmitter {

  _listeners: {
    [ string ]: Array<Listener | null>
  } = {};


  /**
   * Возвращает список всех событий, на которых имеются подписчики
   *
   * @readonly
   * @type {string[]}
   * @memberof EventEmitter
   */
  get listenersList(): string[] {
    return Object.keys( this._listeners )
      .filter( ( eventName: string ) => {
        const eventListeners = this._listeners[ eventName ]
        return isArr( eventListeners ) && eventListeners.length !== 0
      } )
  }

  /**
   * Подписка на события
   *
   * @param {string} eventName
   * @param {Listener} callback
   * @memberof EventEmitter
   */
  on( eventName: string, callback: Listener ) {
    this._listeners[ eventName ] = castArr( this._listeners[ eventName ] )
    this._listeners[ eventName ].push( callback )
  }


  /**
   * Вызов события
   *
   * @param {string} eventName
   * @param {AnyMap} data
   * @memberof EventEmitter
   */
  emit( eventName: string, data: AnyMap ) {
    const lisArr = this._listeners[ eventName ]
    if ( !isArr( lisArr ) ) return

    lisArr.forEach( ( cb: Listener | null ) => {
      if ( isF( cb ) ) { cb( data ) }
    } )
  }



  /**
   * Удаляет подписку(и) на событие(я)
   * Если имеются оба параметра удаляется конкретный слушатель
   * Если только имя - удаляет все слушатели одного события
   * Вызов без аргументов удалит все подписки
   *
   * @param {?string} eventName
   * @param {?Listener} callback
   * @memberof EventEmitter
   */
  off( eventName: ?string, callback: ?Listener ) {
    if ( isStr( eventName ) ) {
      if ( isF( callback ) ) {
        this._offOneListener( eventName, callback )
      } else {
        this._offEventListeners( eventName )
      }
    } else {
      this._offAll()
    }
  }



  _offOneListener( eventName: string, callback: Listener ) {
    const lisArr = this._listeners[ eventName ]
    if ( !isArr( lisArr ) ) return
    const cbIndInArr = lisArr.indexOf( callback )
    if ( cbIndInArr === -1 ) return
    lisArr[ cbIndInArr ] = null
    lisArr.splice( cbIndInArr, 1 )
  }


  _offEventListeners( eventName: string ) {
    const lisArr = this._listeners[ eventName ]
    if ( !isArr( lisArr ) ) return
    for ( let j = 0; j < lisArr.length; j++ ) {
      lisArr[ j ] = null
    }
    delete this._listeners[ eventName ]
  }


  _offAll() {
    Object.keys( this._listeners ).forEach( ( eventName: string ) => {
      this._offEventListeners( eventName )
    } )
  }



  destroy() {
    this._offAll()
  }



}

