// @flow

import { isObj, isStr } from './is'
import { parseObj as parseJson } from './json'
import { hasProp } from './obj'
import prepareBody, { type AsType } from './prepareRequestBody'
import type {
  StringsMap, AnyMap, HttpMethods, RespType
} from './_types'

type InputOptsType = {|
  baseUrl: string,
  defaultHeaders?: StringsMap
|}

type AddonRequestOptsType = {|
  headers?: StringsMap,
  as?: AsType,
  query?: AnyMap
|}


/**
 * Отправка запросов
 *
 * @export
 * @class Rest
 */
export default class Rest {
  /**
   * Адрес api без роутов
   *
   * @type {string}
   * @example
   * https://api.addreality.com
   * @memberof Rest
   */
  baseUrl: string

  /**
   * Заголовки, отправляемые с каждым запросом
   *
   * @type {StringsMap}
   * @example
   * {
   *   'X-Access-Token': 'qwfg34gtrb
   * }
   * @memberof Rest
   */
  defaultHeaders: StringsMap


  /**
   *Creates an instance of Rest.
   * @param {{ baseUrl: string, defaultHeaders?: StringsMap }} opts
   * @memberof Rest
   */
  constructor( opts: InputOptsType ) {
    this.baseUrl = opts.baseUrl.replace( /\/$/, '' )
    this.defaultHeaders = isObj( opts.defaultHeaders ) ? opts.defaultHeaders : {}
  }

  /**
   * Возвращает полный url
   *
   * @param {string} url
   * @param {mixed} [query]
   * @example
   * this.getUrl( '/devices', '?status=1' )
   * // https://api.addreality.com/devices?status=1
   * @returns {string}
   * @memberof Rest
   */
  getUrl( url: string, query?: mixed ): string {
    return this.baseUrl + url + ( isStr( query ) ? query : '' )
  }

  _getHeaders( headers?: StringsMap ): StringsMap {
    return Object.assign( {}, this.defaultHeaders, isObj( headers ) ? headers : {} )
  }

  // eslint-disable-next-line max-params
  _prepareAndSend( method: HttpMethods, urlIn: string, bodyIn?: ?AnyMap, optsIn?: AddonRequestOptsType ) {
    const opts = isObj( optsIn ) ? optsIn : {}
    const query = prepareBody( { body: opts.query, as: 'query' } )
    const url = this.getUrl( urlIn, query )
    return window.fetch( url, {
      method,
      headers: this._getHeaders( opts.headers ),
      body: prepareBody( { body: bodyIn, as: opts.as } )
    } )
  }



  static _handleResp( promise: Promise<Response> ): Promise<RespType> {
    const unknowErr = { code: -1, message: 'unknown error' }

    let sts

    return promise.then( ( resp: Response ) => {
      sts = resp.status
      return resp.text()
    } )
      .then( ( respText: string ) => {
        const parsed = respText === '' ? {} : parseJson( respText )
        const isParsedNull = parsed === null
        const data = parsed === null ? {} : parsed

        // const isSplitterResp = hasProp( data, 'response' ) || hasProp( data, 'error' )

        const hasErr = hasProp( data, 'error' ) || sts !== 200

        if ( hasErr ) {
          let errData: AnyMap = isParsedNull ? unknowErr : data
          if ( hasProp( data, 'error' ) ) {
            // $FlowFixMe
            errData = data.error
          }
          return {
            hasErr,
            status: sts,
            respText,
            error: errData
          }
        }
        return {
          hasErr,
          status: sts,
          respText,
          // $FlowFixMe
          data: hasProp( data, 'response' ) ? data.response : data
        }
      } )
      .catch( ( error: AnyMap ) => ( {
        hasErr: true,
        status: sts,
        respText: '',
        error
      } ) )
  }


  /**
   * Метод GET-запроса
   *
   * @param {string} urlIn
   * @param {?AnyMap} bodyIn
   * @param {{ headers?: StringsMap, as?: AsType, query?: AnyMap }} [optsIn]
   * @returns
   * @memberof Rest
   */
  get( urlIn: string, bodyIn: ?AnyMap, optsIn?: AddonRequestOptsType ) {
    const opts = isObj( optsIn ) ? optsIn : {}
    const url = this.getUrl( urlIn, prepareBody( { body: bodyIn, as: 'query' } ) )
    return Rest._handleResp( window.fetch( url, {
      method: 'GET',
      headers: this._getHeaders( opts.headers ),
      body: null
    } ) )
  }

  /**
   * Метод POST-запроса
   *
   * @param {string} url
   * @param {?AnyMap} body
   * @param {{ headers?: StringsMap, as?: AsType, query?: AnyMap }} [opts]
   * @returns
   * @memberof Rest
   */
  post( url: string, body: ?AnyMap, opts?: AddonRequestOptsType ) {
    return Rest._handleResp( this._prepareAndSend( 'POST', url, body, opts ) )
  }


  /**
   * Метод PUT-запроса
   *
   * @param {string} url
   * @param {?AnyMap} body
   * @param {{ headers?: StringsMap, as?: AsType, query?: AnyMap }} [opts]
   * @returns
   * @memberof Rest
   */
  put( url: string, body: ?AnyMap, opts?: AddonRequestOptsType ) {
    return Rest._handleResp( this._prepareAndSend( 'PUT', url, body, opts ) )
  }



  /**
   * Метод DELETE-запроса
   *
   * @param {string} url
   * @param {?AnyMap} body
   * @param {?{ headers?: StringsMap, as?: AsType, query?: AnyMap }} [opts]
   * @returns
   * @memberof Rest
   */
  del( url: string, body: ?AnyMap, opts?: AddonRequestOptsType ) {
    return Rest._handleResp( this._prepareAndSend( 'DELETE', url, body, opts ) )
  }

}

// @flow



class Option<T> {
  _isValid: boolean = false
  _value: T | null = null

  constructor( val: T ) {
    this._value = val
  }

  isValid(): boolean {
    return this._isValid
  }

  hasErr(): boolean {
    return !this._isValid
  }

  unwrap(): T | null {
    return this._value
  }


  unwrapOr( some: T ): T {
    return this.hasErr() ? some : some
  }

}


window.qwe = new Option( '3' )
const x = window.qwe.unwrapOr( {} )
console.log( 'x: ', x )





export default Option

// @flow

import { castObj, hasProp } from '../obj'

import {
  getNativeType,
  getItem,
  setItem,
  removeItem
} from './funcs'


import type {
  MixedMap
} from '../_types'

import type {
  AdapterContructorArgsType,
  NativeStorageType,
  GetItemReturnType,
  GetPropReturnType,
  SetOrRemovePropReturnType
} from './_types'



export default class StorageAdapter {
  name: string;
  type: NativeStorageType;


  constructor( arg: AdapterContructorArgsType ) {
    this.name = arg.name
    this.type = getNativeType( arg.type )
  }

  getItem(): GetItemReturnType {
    return getItem( this.name, this.type )
  }

  setItem( val: * ): boolean {
    return setItem( this.name, val, this.type )
  }

  removeItem(): boolean {
    return removeItem( this.name, this.type )
  }


  getProp( propName: string ): GetPropReturnType {
    const itemResult = this.getItem()
    if ( itemResult.isValid && !itemResult.isEmpty ) {
      return { hasProp: true, value: itemResult.value[ propName ] }
    }

    return { hasProp: false, value: null }
  }


  setProp( propName: string, val: * ): SetOrRemovePropReturnType {
    const itemResult = this.getItem()
    const itemVal = castObj( itemResult.value )

    if ( val === itemVal[ propName ] ) {
      return {
        isWritten: false,
        reason: 'not changed'
      }
    }

    itemVal[ propName ] = val

    return this._write( itemVal, [ propName ] )
  }


  setProps( newProps: { [ string ]: * } ): SetOrRemovePropReturnType {
    const itemResult = this.getItem()
    const itemVal = castObj( itemResult.value )

    const newPropNames = Object.keys( newProps )
    const changedPropsNames = newPropNames
      .filter( ( propName: string ) => itemVal[ propName ] !== newProps[ propName ] )

    if ( changedPropsNames.length === 0 ) {
      return {
        isWritten: false,
        reason: 'not changed'
      }
    }

    changedPropsNames.forEach( ( propName: string ) => {
      itemVal[ propName ] = newProps[ propName ]
    } )

    return this._write( itemVal, changedPropsNames )
  }


  removeProp( propName: string ): SetOrRemovePropReturnType {
    const itemResult = this.getItem()
    if (
      !itemResult.isValid
      || itemResult.isEmpty
      || !hasProp( itemResult.value, propName )
    ) return { isWritten: false, reason: 'has no prop' }

    const itemVal = itemResult.value
    delete itemVal[ propName ]

    return this._write( itemVal, [ propName ] )

  }



  removeProps( propNames: string[] ): SetOrRemovePropReturnType {
    const itemResult = this.getItem()

    if (
      !itemResult.isValid
      || itemResult.isEmpty
    ) return { isWritten: false, reason: 'has no prop' }

    const itemVal = itemResult.value


    const propsToRemove = propNames.filter( ( propName: string ) => hasProp( itemVal, propName ) )

    if ( propsToRemove.length === 0 ) return { isWritten: false, reason: 'has no prop' }

    propsToRemove.forEach( ( propName: string ) => {
      delete itemVal[ propName ]
    } )


    return this._write( itemVal, propsToRemove )

  }


  _write( newItemVal: MixedMap, affectedProps: string[] ) {
    const isWritten = this.setItem( newItemVal )

    return isWritten
      ? { isWritten: true, affectedProps }
      : { isWritten: false, reason: 'stringify error' }
  }


  destroy() {
    return this
  }




}

// @flow

import EventEmitter from '../EventEmitter'
import { castObj } from '../obj'
import { isObj } from '../is'

import Adapter from './Adapter'

import type { InferenceMap } from '../_types'

import type {
  AdapterContructorArgsType,
  GetItemReturnType,
  GetPropReturnType
} from './_types'



export default class StorageAdapterWithEvents extends EventEmitter {

  adapter: Adapter;

  constructor( arg: AdapterContructorArgsType ) {
    super()
    this.adapter = new Adapter( arg )
  }

  getItem(): GetItemReturnType {
    return this.adapter.getItem()
  }

  setItem( val: * ) {
    this.adapter.setItem( val )

    if ( isObj( val ) ) {
      this._calcChangedAndEmit( val, true )
    }
    this._emitChange()
  }

  removeItem() {
    this.adapter.removeItem()
    this._calcChangedAndEmit( {}, true )
    this._emitChange()
  }


  getProp( propName: string ): GetPropReturnType {
    return this.adapter.getProp( propName )
  }


  setProp( propName: string, val: * ) {
    this.adapter.setProp( propName, val )
    this._calcChangedAndEmit( { [ propName ]: val }, false )
    this._emitChange()
  }

  setProps( newProps: { [ string ]: * } ) {
    this.adapter.setProps( newProps )
    this._calcChangedAndEmit( newProps, false )
    this._emitChange()
  }

  removeProp( propName: string ) {
    this.adapter.removeProp( propName )
    this._calcChangedAndEmit( { [ propName ]: undefined }, false )
    this._emitChange()
  }

  removeProps( propNames: string[] ) {
    this.adapter.removeProps( propNames )

    const emptyProps = {}
    propNames.forEach( ( propName: string ) => {
      emptyProps[ propName ] = undefined
    } )

    this._calcChangedAndEmit( emptyProps, false )
    this._emitChange()
  }


  _emitChange() {
    this.emit( 'change', { adapter: this.adapter } )
  }


  _calcChangedAndEmit( newItem: InferenceMap, isReplacing: boolean ) {
    const oldItemResult = this.adapter.getItem()
    const oldItem = castObj( oldItemResult.value )

    const newItemPropNames = Object.keys( newItem )
    const oldItemPropNames = Object.keys( oldItem )

    const propsToRemove = !isReplacing ? [] : oldItemPropNames
      .filter( ( propName: string ) => !newItemPropNames.includes( propName ) )

    newItemPropNames.concat( propsToRemove ).forEach( ( propName: string ) => {
      this.emit( `change:${propName}`, {
        value: newItem[ propName ],
        oldValue: oldItem[ propName ],
        adapter: this.adapter
      } )
    } )

  }


  destroy() {
    this.off()
    delete this.adapter
  }

}

// @flow

import { isF } from '../is'
import Adapter from './Adapter'
import AdapterWithEvents from './AdapterWithEvents'

import type {
  NativeStorageType,
  GetStorageInCollReturnType
} from './_types'

const isSt = (
  st: ?Adapter | ?AdapterWithEvents
): boolean => st instanceof Adapter || st instanceof AdapterWithEvents

export default class StoragesCollection {
  storages: { [ string ]: Adapter | AdapterWithEvents } = {};

  register( arg: {
    name: string,
    type?: NativeStorageType,
    withEvents?: boolean
  } ): Adapter | AdapterWithEvents {

    const { name, type, withEvents } = arg

    if ( isSt( this.storages[ name ] ) ) {
      return this.storages[ name ]
    }

    const args = { name, type }
    const newInstance = withEvents ? new AdapterWithEvents( args ) : new Adapter( args )
    this.storages[ name ] = newInstance
    return newInstance
  }


  unregister( name: string ) {
    if ( !isSt( this.storages[ name ] ) ) return

    if ( isF( this.storages[ name ].destroy ) ) {
      this.storages[ name ].destroy()
    }

    delete this.storages[ name ]

  }


  get( name: string ): GetStorageInCollReturnType {
    if ( isSt( this.storages[ name ] ) ) {
      return {
        isRegistered: true,
        storage: this.storages[ name ]
      }
    }

    return { isRegistered: false }
  }


}

// @flow

import type { MixedMap } from '../_types'

export type NativeStorageType = 'localStorage' | 'sessionStorage'

export type AdapterContructorArgsType = {
  name: string,
  type?: NativeStorageType
}


export type GetItemReturnType = {
  isValid: true,
  value: MixedMap,
  isEmpty: boolean,
  isStorageHasItem: boolean,
} | {
  isValid: false,
  value: null,
  isEmpty: boolean,
  isStorageHasItem: boolean,
}


export type GetPropReturnType = {
  hasProp: false,
  value: null
} | {
  hasProp: true,
  value: *
}

export type SetOrRemovePropReturnType = {
  isWritten: true,
  affectedProps: string[]
} | {
  isWritten: false,
  reason: 'stringify error' | 'not changed' | 'has no prop'
}


export type GetStorageInCollReturnType = {
  isRegistered: false
} | {
  isRegistered: true,
  storage: *
}

// @flow

import { isStr, isNull } from '../is'
import { parseObj, str as jsonStr } from '../json'

import type {
  NativeStorageType,
  GetItemReturnType
} from './_types'

const DEFAULT_STORAGE_TYPE = 'localStorage'
const nativeTypes: NativeStorageType[] = [ 'localStorage', 'sessionStorage' ]
const PRIVATE_NAME_PREFIX = '__ar__'


export function getNativeType( maybeType: ?NativeStorageType ): NativeStorageType {
  return ( isStr( maybeType ) && nativeTypes.includes( maybeType ) )
    ? maybeType
    : DEFAULT_STORAGE_TYPE
}




export function toPrivateName( name: string ) {
  return name.indexOf( PRIVATE_NAME_PREFIX ) === 0
    ? name
    : PRIVATE_NAME_PREFIX + name
}


// const PRIVATE_NAME_PREFIX_REGEX = /^__ar__/
// export function fromPrivateName( name: string ) {
//   return name.replace( PRIVATE_NAME_PREFIX_REGEX, '' )
// }





export function getItem(
  name: string,
  type?: NativeStorageType,
): GetItemReturnType {

  const result: GetItemReturnType = {
    isValid: false,
    value: null,
    isStorageHasItem: false,
    isEmpty: true
  }
  const storage: Storage = window[ getNativeType( type ) ]
  const item = storage.getItem( toPrivateName( name ) )

  if ( isNull( item ) ) return result

  result.isStorageHasItem = true

  const parsedItem = parseObj( item )

  if ( isNull( parsedItem ) ) return result

  return {
    isValid: true,
    value: parsedItem,
    isStorageHasItem: true,
    isEmpty: Object.keys( parsedItem ).length === 0
  }

}



export function setItem(
  name: string,
  value: *,
  type?: NativeStorageType
): boolean {
  const storage: Storage = window[ getNativeType( type ) ]
  const val = isStr( value ) ? value : jsonStr( value )
  if ( val === null ) return false

  storage.setItem( toPrivateName( name ), val )

  return true
}




export function removeItem(
  name: string,
  type?: NativeStorageType
): boolean {
  const storage: Storage = window[ getNativeType( type ) ]
  storage.removeItem( toPrivateName( name ) )
  return true
}

// @flow

import Adapter from './Adapter'
import AdapterWithEvents from './AdapterWithEvents'
import StoragesCollection from './StoragesCollection'


import {
  getItem,
  setItem,
  removeItem,
  getNativeType,
  toPrivateName
} from './funcs'


import type {
  AdapterContructorArgsType,
  NativeStorageType
} from './_types'


export default class Storages {

  static createAdapter( arg: AdapterContructorArgsType ) {
    return new Adapter( arg )
  }

  static createAdapterWithEvents( arg: AdapterContructorArgsType ) {
    return new AdapterWithEvents( arg )
  }

  static createStoragesCollection() {
    return new StoragesCollection()
  }

  static getItem( name: string, type?: NativeStorageType ) {
    return getItem( name, type )
  }
  static setItem( name: string, value: *, type?: NativeStorageType ) {
    return setItem( name, value, type )
  }
  static removeItem( name: string, type?: NativeStorageType ) {
    return removeItem( name, type )
  }
  static getNativeType( maybeType: ?NativeStorageType ) {
    return getNativeType( maybeType )
  }
  static toPrivateName( name: string ) {
    return toPrivateName( name )
  }

}

// @flow

import EventEmitter from '../EventEmitter'
import { isNull, isStr, isNum } from '../is'
import {
  getNow,
  isDate,
  tsToSec,
  get,
  // set,
  getHmsFromDate,
  getObjDateFromDate,
  getLastDateInMonth
} from './misc'


import setTimePartToStartEnd from './setTimePartToStartEnd'

import convert, { convertFromObj } from './convert'
import type {
  FullDateType,
  ObjDateType,
  ObjDateKeysType,
  PartNameType,
  ObjHmsType
} from './_types'



export default class TimeDate extends EventEmitter {
  _date: Date
  _min: number | null = null
  _max: number | null = null

  static fromNow(): TimeDate {
    return new TimeDate( getNow() )
  }
  static from( arg: FullDateType ): TimeDate {
    return new TimeDate( arg )
  }

  constructor( arg: FullDateType ) {
    super()
    const d = convert( arg )
    if ( isNull( d ) ) {
      console.error( 'date is not valid: ', arg )
    }
    this._date = isNull( d ) ? getNow() : d
  }



  _setValidDateBetweenMinMax( d: Date ) {
    const { min, max } = this
    if ( isNull( min ) && isNull( max ) ) {
      return
    }

    if ( isNum( min ) && d.getTime() < min ) {
      d.setTime( min )
      console.warn( d, ' is less then min value' )
    }

    if ( isNum( max ) && d.getTime() > max ) {
      d.setTime( max )
      console.warn( d, ' is more then max value' )
    }

  }


  _emitChange( partName?: PartNameType ) {
    if ( isStr( partName ) ) {
      this.emit( `change:${partName}`, {
        date: this.date,
        partName,
        partValue: this.get( partName )
      } )
    } else {
      this.emit( 'change', { date: this.date } )
    }
  }



  get( type: PartNameType ): number {
    return get( this.date, type )
  }

  setDate( arg: FullDateType ) {
    const d = convert( arg )
    if ( !isNull( d ) ) {
      this._setValidDateBetweenMinMax( d )
      this._date = d
      this._emitChange()
    }
  }


  setParts( args: { [ ObjDateKeysType ]: number } ) {
    const d = convertFromObj( args, this.date )
    if ( d === null ) {
      return
    }
    this._setValidDateBetweenMinMax( d )
    this._date = d
    Object.keys( args ).forEach( ( partName: ObjDateKeysType ) => {
      this._emitChange( partName )
    } )
    this._emitChange()
  }

  get date(): Date {
    return isDate( this._date ) ? this._date : getNow()
  }

  get ts(): number {
    return this.date.getTime()
  }

  get tsInSec(): number {
    return tsToSec( this.ts )
  }

  get objHms(): ObjHmsType {
    return getHmsFromDate( this.date )
  }

  get objDate(): ObjDateType {
    return getObjDateFromDate( this.date )
  }

  get min() {
    return this._min
  }
  set min( arg: FullDateType | null ) {
    if ( arg === null ) { this._min = null; return }
    const d = convert( arg )
    if ( !isNull( d ) ) { this._min = d.getTime() }
  }

  get max() {
    return this._max
  }
  set max( arg: FullDateType | null ) {
    if ( arg === null ) { this._max = null; return }
    const d = convert( arg )
    if ( !isNull( d ) ) { this._max = d.getTime() }
  }


  get lastDateInMonth(): number {
    return getLastDateInMonth( this.date )
  }

  setTimePartToStartEnd( partName: ObjDateKeysType, startOrEnd: 'start' | 'end' ) {
    const d = setTimePartToStartEnd( this.date, partName, startOrEnd )
    this._setValidDateBetweenMinMax( d )
    this._date = d
    this._emitChange()
  }



  // setTimeToNow() {

  // }

}

