export type StrArrHmsType = [ string, string, string ]
export type HmsType =
| ObjHmsType
| StrObjHmsType
| ArrHmsType
| StrArrHmsType



export type ObjDateType = ObjHmsType & { y: number, M: number, d: number }
export type StrObjDateType = StrObjHmsType & { y: string, M: string, d: string }

export type HmsKeysType = $Keys<ObjHmsType>
export type ObjDateKeysType = $Keys<ObjDateType>

