import { isStr, isNum, isObj } from '../is'
import {
  isDate, tsToMs, set, getNow
} from './misc'
import { objDateKeys } from './consts'
import type { FullDateType, ObjDateKeysType } from './_types'



/**
 * Конвертирует строку в Date
 *
 * @export
 * @param {string} dateStr
 * @example
 * convertFromStr( '2018-06-09T15:15:14.048Z' ) // Date
 * convertFromStr( 'qwe' ) // null
 * @returns {(Date | null)}
 */
export function convertFromStr( dateStr: string ): Date | null {
  const d = new Date( dateStr )
  return isNum( d.getTime() ) ? d : null
}



export function convertFromObj(
  dateObj: { [ ObjDateKeysType ]: number },
  dateToChange?: Date
): Date | null {
  let keys = Object.keys( dateObj )

  if ( !keys.some( ( argKey: string ) => objDateKeys.includes( argKey ) ) ) {
    console.error( 'argument for converting to object is invalid: ', dateObj )
    return null
  }

  keys = keys.filter( ( objKey: string ) => objDateKeys.includes( objKey ) )

  keys.sort( ( argKeyA: string, argKeyB: string ): number => {
    const indA = objDateKeys.indexOf( argKeyA )
    const indB = objDateKeys.indexOf( argKeyB )
    return indA - indB
  } )

  const d = isDate( dateToChange ) ? new Date( dateToChange ) : getNow()

  keys.forEach( ( key: ObjDateKeysType ) => {
    set( d, key, Math.floor( dateObj[ key ] ) )
  } )
  if ( keys.includes( 'M' ) ) {
    set( d, 'M', Math.floor( dateObj.M ) )
  }
  return d
}



export default function convert( date: FullDateType ): Date | null {
  if ( isStr( date ) ) {
    return convertFromStr( date )
  } if ( isNum( date ) ) {
    return new Date( tsToMs( date ) )
  } if ( isDate( date ) ) {
    return date
  } if ( isObj( date ) ) {
    return convertFromObj( date )
  }

  return null
}

