import { isArr } from '../is'

type ValType = number | string | void
export type ArrHmsType = ValType[]
export type MaybeValidObjHmsType = { h: ValType, m: ValType, s: ValType }
export type HmsType = ArrHmsType | MaybeValidObjHmsType



const getFromArr = ( hmsArr: ArrHmsType ): MaybeValidObjHmsType => {
  let arr = hmsArr.slice( 0 )
  if ( arr.length !== 3 ) {
    arr = new Array( 3 )
      .fill( 0 )
      .map( ( val: *, ind: number ) => hmsArr[ ind ] || 0 )
  }
  const [ h, m, s ] = hmsArr
  return { h, m, s }
}

const getVal = ( val: ValType ): number => {
  const parsed = parseInt( val )
  return Number.isNaN( parsed ) ? 0 : parsed
}


/**
 * Возвращает количество секунд. Принимает объект или массив часов, минут, секунд
 *
 * @export
 * @param {HmsType} hms
 * @example
 * hmsToSec( { h: 0, m: 2, s: 3 } ) // 123
 * hmsToSec( { h: '00', m: '02', s: '03' } ) // 123
 * hmsToSec( [ 0, 2, 3 ] ) // 123
 * hmsToSec( [ '00', '02', '03' ] ) // 123
 * @returns {number} seconds
 */
export default function ( hms: HmsType ): number {
  let seconds = 0
  const d: MaybeValidObjHmsType = isArr( hms ) ? getFromArr( hms ) : hms

  seconds += getVal( d.h ) * 3600
  seconds += getVal( d.m ) * 60
  seconds += getVal( d.s )

  return seconds
}

