  ObjHmsType
} from './_types'


/**
 * Делает строку из числа, плюч добавляет 0 впереди, если число меньше 10
 *
 * @export
 * @param {number} num
 * @example
 * toStr( 59 ) // '59'
 * toStr( 1 ) // '01'
 * @returns {string}
 */
export function toStr( num: number ): string {
  return num < 10 ? `0${num}` : `${num}`
}


/**
 * Возвращает дату с текущим временем в нулевой временной зоне
 *
 * @export
 * @example
 * // now is 17:00
 * const d = new Date() // 2018-06-06T14:00:00.612Z
 * d.getHours() // 17
 * d.getUTCHours() // 14
 * getNow().getUTCHours() // 17
 * @returns {Date}
 */
export function getNow(): Date {
  const d = new Date()
  d.setMinutes( d.getMinutes() - d.getTimezoneOffset() )
  return d
}

/**
 * isDate
 *
 * @export
 * @param {*} maybeDate
 * @returns {boolean}
 */
export function isDate( maybeDate: * ): boolean %checks {
  return maybeDate instanceof Date
}


export type CheckTimestampReturnType =
| {|
  isValid: false
|}
| {|
  isValid: true,
  isInSec: boolean,
  isInMs: boolean,
  ts: number
|}


/**
 * Проверяет timestamp в секундах или милисекундах
 *
 * @export
 * @param {?number} ts
 * @example
 * checkTimestamp( '123' )
 * // { isValid: false }
 *
 * checkTimestamp( 1528547903882 )
 * // { isValid: true, isInSec: false, isInMs: true, ts: 1528547903882 }
 *
 * checkTimestamp( 1528547903 )
 * // { isValid: true, isInSec: true, isInMs: false, ts: 1528547903 }
 * @returns {CheckTimestampReturnType}
 */
export function checkTimestamp( ts: ?number ): CheckTimestampReturnType {
  if ( !isNum( ts ) ) return { isValid: false }
  const asStr = ts.toString()
  const isLength13 = asStr.length === 13
  return {
    isValid: true,
    isInSec: !isLength13,
    isInMs: isLength13,
    ts
  }
}


/**
 * Переводит timestamp в секунды
 *
 * @export
 * @param {number} ts
 * @example
 * tsToSec( 1528304655612 ) // 1528304656
 * tsToSec( 1528304656 ) // 1528304656
 * tsToSec( 'asd' ) // 0
 * @returns {number}
 */
export function tsToSec( ts: number ): number {
  const checkResult = checkTimestamp( ts )
  if ( !checkResult.isValid ) {
    console.warn( 'ts is not valid', ts )
    return 0
  } if ( checkResult.isInSec ) {
    return ts
  }
  return Math.round( ts / 1000 )
}

/**
 * Переводит timestamp в милисекунды
 *
 * @export
 * @param {number} ts
 * @example
 * tsToMs( 1528304656 ) // 1528304655612
 * tsToMs( 1528304655612 ) // 1528304655612
 * tsToMs( 'asd' ) // 0
 * @returns {number}
 */
export function tsToMs( ts: number ): number {
  const checkResult = checkTimestamp( ts )
  if ( !checkResult.isValid ) {
    console.warn( 'ts is not valid', ts )
    return 0
  }

  return checkResult.isInSec ? ts * 1000 : ts
}



/**
 * Дубоирует функционал Date.getUTC*
 *
 * @export
 * @param {Date} d
 * @param {PartNameType} partName
 * @example
 * get( date, 'y' ) // 2018
 * get( date, 'd' ) // 31
 * @returns {number}
 */
export function get( d: Date, partName: PartNameType ): number {
  switch ( partName ) {
    case 'y':
      return d.getUTCFullYear()
    case 'M':
      return d.getUTCMonth()
    // TODO: week
    // case 'w':
    //   return d.getTime()
    case 'd':
      return d.getUTCDate()
    case 'D':
      return d.getUTCDay()
    case 'h':
      return d.getUTCHours()
    case 'm':
      return d.getUTCMinutes()
    case 's':
      return d.getUTCSeconds()
    case 'ms':
      return d.getUTCMilliseconds()
    default:
      throw new Error( `unknown time part name: ${partName}` )
  }
}



/**
 * Дублирует функционал Date.setUTC*
 *
 * @export
 * @param {Date} d
 * @param {PartNameType} partName
 * @param {...number[]} args
 * @example
 * set( date, 'h', 23 ) // undefined
 * set( date, 'm', 59 )
 */
export function set( d: Date, partName: PartNameType, ...args: number[] ) {
  switch ( partName ) {
    case 'y':
      d.setUTCFullYear( ...args )
      break
    case 'M':
      d.setUTCMonth( ...args )
      break
    case 'd':
      d.setUTCDate( ...args )
      break
    case 'h':
      d.setUTCHours( ...args )
      break
    case 'm':
      d.setUTCMinutes( ...args )
      break
    case 's':
      d.setUTCSeconds( ...args )
      break
    case 'ms':
      d.setUTCMilliseconds( ...args )
      break
    default:
      throw new Error( `unknown time part name: ${partName}` )
  }
}


/**
 * Возвращает объект с часами, минутами, секундами из переданной даты
 *
 * @export
 * @param {Date} d
 * @example
 * getHmsFromDate( date )
 * // {
 * //   h: 13,
 * //   m: 45
 * //   s: 59
 * // }
 * @returns {ObjHmsType}
 */
export function getHmsFromDate( d: Date ): ObjHmsType {
  return {
    h: get( d, 'h' ),
    m: get( d, 'm' ),
    s: get( d, 's' )
  }
}


/**
 * Возвращает дату в виде объекта
 *
 * @export
 * @param {Date} d
 * @example
 * getObjDateFromDate( date )
 * // {
 * //   y: 2018,
 * //   M: 1,
 * //   d: 28,
 * //   h: 13,
 * //   m: 45
 * //   s: 59
 * // }
 * @returns {ObjHmsType}
 */
export function getObjDateFromDate( d: Date ): ObjDateType {
  return {
    y: get( d, 'y' ),
    M: get( d, 'M' ),
    d: get( d, 'd' ),
    ...getHmsFromDate( d )
  }
}



/**
 * Возвращает последнее число месяца в переданной дате
 *
 * @export
 * @param {Date} date
 * @example
 * getLastDateInMonth( date ) // 31
 * getLastDateInMonth( date2 ) // 28
 * @returns {number}
 */
export function getLastDateInMonth( date: Date ): number {
  const d = new Date( date )
  set( d, 'M', get( d, 'M' ) + 1 )
  set( d, 'd', 0 )
  return get( d, 'd' )
}







/**
 * Возвращает количество дней между двумя датами
 *
 * @export
 * @param {Date} date1
 * @param {Date} date2
 * @returns {number}
 */
export function daysBetween( date1: Date, date2: Date ): number {
  const differenceMs = Math.abs( date1.getTime() - date2.getTime() )

  return Math.round( differenceMs / ONE_DAY_MS )
}

