// @flow

import { toStr } from './misc'
import type {
  ObjHmsType,
  StrObjHmsType,
  ArrHmsType,
  StrArrHmsType
} from './_types'

const keys = [ 'h', 'm', 's' ]

/**
 * Переводит секунды в часы, минуты, секунды
 *
 * @export
 * @param {number} seconds
 * @example
 * secToHms( 123456 ) // { h: 10, m: 17, s: 36 }
 * @returns {{ h: number, m: number, s: number }}
 */
export function secToHms( seconds: number ): ObjHmsType {
  const sec = parseInt( seconds )

  const h = Math.floor( sec / 3600 ) % 24
  const m = Math.floor( sec / 60 ) % 60
  const s = sec % 60

  return { h, m, s }
}

/**
 * Переводит секунды в часы, минуты, секунды
 * Возвращает объект с форматированными строками
 *
 * @export
 * @param {number} seconds
 * @example
 * secToHms_str( 123 ) // { h: '00', m: '02', s: '03' }
 * @returns {{ h: string, m: string, s: string }}
 */
export function secToHms_str( seconds: number ): StrObjHmsType {
  const d = secToHms( seconds )
  const result = {}
  keys.forEach( ( key: 'h' | 'm' | 's' ) => {
    result[ key ] = toStr( d[ key ] )
  } )
  return result
}

/**
 * Возвращает массив с часами, минутами, секундами
 *
 * @export
 * @param {number} seconds
 * @example
 * secToHms_arr( 123456 ) // [ 10, 17, 36 ]
 * @returns {[ number, number, number ]}
 */
export function secToHms_arr( seconds: number ): ArrHmsType {
  const d = secToHms( seconds )
  return [ d.h, d.m, d.s ]
}

/**
 * Возвращает массив с форматированными часами, минутами, секундами
 *
 * @export
 * @param {number} seconds
 * @example
 * secToHms_arrStr( 123 ) // [ '00', '02', '03' ]
 * @returns {[ string, string, string ]}
 */
export function secToHms_arrStr( seconds: number ): StrArrHmsType {
  const [ h, m, s ] = secToHms_arr( seconds )
  return [ toStr( h ), toStr( m ), toStr( s ) ]
}

