import { isNull } from '../is'
import { convertFromObj } from './convert'
import { objDateKeys } from './consts'
import type { ObjDateKeysType } from './_types'


function getKeysToChange( ind: number ): ObjDateKeysType[] {
  return objDateKeys
    .filter( ( name: ObjDateKeysType, i: number ) => i > ind )
}



const maxValsOfPartNames = {
  M: 11,
  d: 31,
  h: 23,
  m: 59,
  s: 59,
  ms: 999
}
function getPartsVal( keysToChange: ObjDateKeysType[], startOrEnd: 'start' | 'end' ) {
  const parts = {}
  if ( startOrEnd === 'start' ) {
    keysToChange.forEach( ( name: ObjDateKeysType ) => {
      parts[ name ] = name === 'd' ? 1 : 0
    } )
  } else {
    keysToChange.forEach( ( name: ObjDateKeysType ) => {
      parts[ name ] = maxValsOfPartNames[ name ]
    } )
  }

  return parts
}


export default function setTimePartToStartEnd(
  d: Date,
  partName: ObjDateKeysType,
  startOrEnd: 'start' | 'end'
): Date {
  const indOfPartName = objDateKeys.indexOf( partName )
  if ( indOfPartName === -1 ) {
    console.error( 'wrond time part name', partName )
    return d
  }

  const keysToChange = getKeysToChange( indOfPartName )

  const parts = getPartsVal( keysToChange, startOrEnd )

  const resultDate = convertFromObj( parts, d )
  return isNull( resultDate ) ? d : resultDate
}

