import Inherent from './inherent'
import {
  validateNumOrNumRangesStr,
  numsToStrWithCollapsing,
  validateNumsArr,
  parseNumOrNumRangesStr,
  isAnyDefault
} from './utils'

import type {
  ValidStrType,
  StrType,
  ValidValType,
  ValType
} from './_types'

import { ValsInterface } from './_types'



/* eslint-disable class-methods-use-this */
export default class DateVal
  extends Inherent implements ValsInterface {

  +name: 'date' = 'date';
  +firstChar: 'D'= 'D';

  /**
   * @param {ValidStrType} str D1,2,3,5-10,31
   * @returns {ValidValType} [ 1, 10, 31 ]
   * @memberof DateVal
   */
  parseStr( str: ValidStrType ): ValidValType {
    return parseNumOrNumRangesStr( str, this.firstChar )
  }

  /**
   * @param {ValidValType} val [ 1, 10, 31 ]
   * @returns {ValidStrType} D1,2,3,5-10,31
   * @memberof DateVal
   */
  stringify( val: ValidValType ): ValidStrType {
    return numsToStrWithCollapsing( val, this.firstChar )
  }


  validateStr( str: StrType ): StrType {
    return validateNumOrNumRangesStr( str, this.firstChar ) ? str : null
  }

  validateVal( val: ValType ): ValType {
    const isValid = validateNumsArr( val, 31, 1 )
    return isValid ? val : null
  }


  get isAny(): boolean {
    return isAnyDefault( this._val, 1, 31 )
  }

}

