import { isStr } from '../is'
import { validateNumsArr, isAnyDefault } from './utils'
import Inherent from './inherent'

import type {
  ValidStrType,
  StrType,
  ValidValType,
  ValType
} from './_types'
import { ValsInterface } from './_types'


const firstDregex = /^d/
const validStrRegex = /^d(0|1){7}$/

/* eslint-disable class-methods-use-this */
export default class DayVal
  extends Inherent implements ValsInterface {

  +name: 'day' = 'day';

  /**
   * @param {ValidStrType} str d0010100
   * @returns {ValidValType} [ 2, 4 ]
   * @memberof DayVal
   */
  parseStr( str: ValidStrType ): ValidValType {
    const result = []
    str.replace( firstDregex, '' )
      .split( '' )
      .forEach( ( dayVal: string, ind: number ) => {
        if ( dayVal === '1' ) {
          result.push( ind )
        }
      } )
    return result
  }

  /**
   * @param {ValidValType} daysArr [ 0, 3, 4, 5 ]
   * @returns {ValidStrType} d1001110
   * @memberof DayVal
   */
  stringify( daysArr: ValidValType ): ValidStrType {
    const result = new Array( 7 ).fill( 0 )
    daysArr.forEach( ( dayInd: number ) => { result[ dayInd ] = 1 } )
    return `d${result.join( '' )}`
  }


  validateStr( str: StrType ): StrType {
    return isStr( str ) && validStrRegex.test( str ) ? str : null
  }

  validateVal( val: ValType ): ValType {
    return validateNumsArr( val, 6 ) ? val : null
  }

  get isAny(): boolean {
    return isAnyDefault( this._val, 0, 6 )
  }

}

