import { isStr, isF } from '../is'
import { debounce } from '../func'

import Time from './time'
import Day from './day'
import Date from './date'
import Month from './month'
import type {
  OnValChangeType, NamesType, ValType, StrType, ValidValType
} from './_types'

// type ValKeyType = 'time' | 'day' | 'date' | 'month'
type ControllerType = Time | Day | Date | Month

type OnChangeArgType = { name: NamesType, str: StrType, val: ValidValType }
type OnChangeType = ( OnChangeArgType ) => void
// eslint-disable-next-line no-use-before-define
type OnChangeCbType = ( OnChangeArgType, TimingParser ) => void

/**
 * TimingParser
 *
 * @export
 * @class TimingParser
 */
export default class TimingParser {
  time: Time
  day: Day
  date: Date
  month: Month

  onValChange: OnValChangeType
  onChange: OnChangeType
  onChangeDebounced: OnChangeType
  onChangeCb: ?OnChangeCbType


  constructor( opts: { onChange?: OnChangeCbType } = {} ) {
    this.onChangeCb = opts.onChange
    this.onChangeDebounced = debounce( ( ...args: * ) => {
      this.onChange( ...args )
    }, 100 )
    const valProps = { onChange: this.onValChange }

    this.time = new Time( valProps )
    this.day = new Day( valProps )
    this.date = new Date( valProps )
    this.month = new Month( valProps )
    return this
  }


  _getCtrl( name: NamesType ): Time | Day | Date | Month | null {
    switch ( name ) {
      case 'time':
      case 'day':
      case 'date':
      case 'month':
        // $FlowFixMe
        return this[ name ]
      default:
        return null
    }
  }

  /**
   * Устанавливает значение определённого типа
   *
   * @param {NamesType} name
   * @param {ValType} val
   * @example
   * this.setVal( 'date', [ 2, 15, 31 ] )
   * @memberof TimingParser
   */
  setVal( name: NamesType, val: ValType ) {
    const ctrl = this._getCtrl( name )
    if ( ctrl === null ) return
    ctrl.setFromVal( val )
  }


  onValChange = ( name: NamesType ) => {
    const ctrl = this._getCtrl( name )

    if ( ctrl === null ) return

    const { val, str } = ctrl

    this.onChange( { name, val, str } )
  }


  onChange = ( arg: OnChangeArgType ) => {
    if ( isF( this.onChangeCb ) ) {
      this.onChangeCb( arg, this )
    }
  }


  get controllers(): ControllerType[] {
    return [ this.time, this.day, this.date, this.month ]
  }

  /**
   * Возвращает все заданные значения в виде одной строки
   * @example
   * this.getStr()
   * // "(19:00:00-19:01:00, 20:20:01-21:30:59); d0010100; D1,2,3,5-10,20; m0,1,2,5-8,11"
   * @returns {string}
   *
   * @memberof TimingParser
   */
  getStr(): string {
    return this.controllers
      .map( ( ctrl: ControllerType ) => ctrl.str )
      .filter( ( maybeStr: string | null ) => maybeStr !== null )
      .join( ';' )
  }

  /**
   * Возвращает все заданные значения в виде массивов
   * @example
   * this.getVals()
   * // {
   * //   time: [ 0, 86399 ],
   * //   day: [ 1, 6 ],
   * //   date: [],
   * //   month: [ 11 ]
   * // }
   * @returns
   * @memberof TimingParser
   */
  getVals() {
    return {
      time: this.time.val,
      day: this.day.val,
      date: this.date.val,
      month: this.month.val
    }
  }

  /**
   * Принимает строку таймингов и задаёт всё указанные значения
   *
   * @param {string} [str] "d0010100; D1,2,3,5-10,20"
   * @returns {TimingParser}
   * @memberof TimingParser
   */
  parseStr( str?: string ): TimingParser {
    if ( !isStr( str ) ) return this
    const strings = str.split( ';' ).map( ( s: string ) => s.trim() )
    const { controllers } = this

    for ( let ind = 0; ind < strings.length; ind++ ) {
      const s = strings[ ind ]
      const strCtrl = controllers.find( ( ctrl: ControllerType ) => ctrl.validateStr( s ) !== null )
      if ( strCtrl !== undefined ) {
        strCtrl.setFromStr( s )
      }
    }

    return this
  }


  /**
   * destroy
   *
   * @memberof TimingParser
   */
  destroy() {
    this.onChangeCb = function () {}
    this.onValChange = function () {}
    this.onChangeDebounced = function () {}
  }

}

