import { isEqual } from '../misc'
import { sortCb_num } from '../func'

import type {
  StrType,
  ValType,
  ValidStrType,
  ValidValType,
  NamesType,
  OnValChangeType
} from './_types'

type PropsType = {
  onChange: OnValChangeType
}

/* eslint-disable class-methods-use-this */
export default class Inherent {
  _str: StrType = null;
  _val: ValidValType = [];

  onChange: OnValChangeType = () => {};

  +name: NamesType = ''

  constructor( { onChange }: PropsType ) {
    this.onChange = onChange
  }

  get str(): StrType {
    return this._str
  }

  get val(): ValidValType {
    return this._val.slice( 0 )
  }

  setFromStr( s: StrType ) {
    const str = this.validateStr( s )
    if ( str === null ) {
      this._str = null
      this._val = []
      return this
    }

    if ( this._str === str ) return this
    this._str = str
    this._val = this.parseStr( str )

    this.onChange( this.name )

    return this
  }

  setFromVal( v: ValType ) {
    const val = this.validateVal( v )
    if ( val === null ) {
      this._val = []
      this._str = null
      return this
    }

    val.sort( sortCb_num )

    if ( isEqual( this._val, val ) ) return this
    this._val = val
    this._str = this.stringify( val )

    this.onChange( this.name )

    return this
  }

  validateStr( s: StrType ): StrType {
    return s
  }

  // eslint-disable-next-line no-unused-vars
  parseStr( s: ValidStrType ): ValidValType {
    return []
  }

  validateVal( v: ValType ): ValType {
    return v
  }

  // eslint-disable-next-line no-unused-vars
  stringify( v: ValidValType ): ValidStrType {
    return ''
  }

  get isAny(): boolean {
    return this.val.length === 0
  }

}

