import { isStr, isArr } from '../is'
import { MAX_SEC_IN_DAY, hmsToSec, secToHms_arrStr } from '../Time'
import Inherent from './inherent'
import { validateNumsArr } from './utils'

import { ValsInterface } from './_types'
import type {
  ValidStrType,
  StrType,
  ValidValType,
  ValType
} from './_types'

type OnePeriodType = [ number, number ]


const parensRegex: RegExp = /^\(|\)$/g
const validatorParensRegex: RegExp = /^\(.*\)$/

/* eslint-disable class-methods-use-this */
export default class TimeVal
  extends Inherent implements ValsInterface {

  +name: 'time' = 'time'
  get defaultTime(): ValidValType {
    return [ 0, MAX_SEC_IN_DAY ]
  }

  secToPeriodPairs( sec: ValidValType ): OnePeriodType[] {
    const periods: OnePeriodType[] = []

    for ( let i = 0; i < sec.length; i++ ) {
      const start = sec[ i ]
      const end = sec[ i + 1 ]
      periods.push( [ start, end ] )
      i++
    }

    return periods
  }

  /**
   * @param {string} hmsStr 21:30:59
   * @returns {number} 85399
   * @memberof TimeVal
   */
  hmsStrToSec( hmsStr: string ): number {
    const hmsArr = hmsStr.split( ':' ).map( ( n: string ) => parseInt( n ) )
    return hmsToSec( hmsArr )
  }

  /**
   * @param {string} hmsPeriodStr 20:20:01-21:30:59
   * @returns {OnePeriodType} [ 200, 379 ]
   * @memberof TimeVal
   */
  hmsStrPeriodToSec( hmsPeriodStr: string ): OnePeriodType {
    const [ start, end ] = hmsPeriodStr.split( '-' )
    return [
      this.hmsStrToSec( start ),
      this.hmsStrToSec( end )
    ]
  }

  /**
   * @param {ValidStrType} s (19:00:00-19:01:00,20:20:01-21:30:59)
   * @returns {ValidValType} [ seconds, seconds, 0, 86399 ]
   * @memberof TimeVal
   */
  parseStr( s: ValidStrType ): ValidValType {
    const seconds = []
    s.trim()
      .replace( parensRegex, '' )
      .split( ',' )
      .forEach( ( strPeriod: string ) => {
        seconds.push( ...this.hmsStrPeriodToSec( strPeriod ) )
      } )
    return seconds
  }


  /**
   * @param {OnePeriodType} periodArr [ 200, 379 ]
   * @returns {string} 00:33:53-03:07:13
   * @memberof TimeVal
   */
  stringifyPeriodArr( periodArr: OnePeriodType ): string {
    const concatHms = ( sec: number ) => secToHms_arrStr( sec ).join( ':' )
    const [ start, end ] = periodArr
    return `${concatHms( start )}-${concatHms( end )}`
  }


  /**
   * @param {ValidValType} seconds [ 200, 379,18131, 79511 ]
   * @returns {ValidStrType} (00:00:00-00:20:30,00:33:53-03:07:13)
   * @memberof TimeVal
   */
  stringify( seconds: ValidValType ): ValidStrType {
    const periods = this.secToPeriodPairs( seconds )
    const periodsStr = periods.map( this.stringifyPeriodArr ).join( ',' )
    return `(${periodsStr})`
  }

  validateStr( s: StrType ): StrType {
    const isValid = isStr( s ) && validatorParensRegex.test( s )
    return isValid ? s : null
  }

  validateVal( val: ValType ): ValType {
    const isValid = isArr( val )
      && val.length > 1
      && val.length % 2 === 0
      && validateNumsArr( val, MAX_SEC_IN_DAY )
    return isValid ? val : null
  }

  get isAny(): boolean {
    const { val } = this
    return val.length === 0 || (
      val.length === 2 && val[ 0 ] === 0 && val[ 1 ] === MAX_SEC_IN_DAY
    )
  }

}

