import { isStr, isArr, isNum } from '../is'
import type {
  ValidStrType,
  StrType,
  ValidValType,
  ValType
} from './_types'
import { sortCb_num } from '../func'

const oneDigitRegex = /\d/
const numRangeRegex = /^\d{1,2}-\d{1,2}$/

type FirstCharsType = 'm' | 'D'


function logErrAndReturnDefault( str: string ) {
  console.error( 'invalid number range ', str )
  return []
}
function expandNumRange( str: string ): number[] {
  if ( !numRangeRegex.test( str ) ) return logErrAndReturnDefault( str )
  const period = str.split( '-' )
  let start = parseInt( period[ 0 ] )
  const end = parseInt( period[ 1 ] )
  if ( start >= end ) return logErrAndReturnDefault( str )
  const nums = []

  while ( start <= end ) {
    nums.push( start )
    start++
  }

  return nums
}





export function validateNumOrNumRangesStr( str: StrType, firstChar: string ): boolean {
  return isStr( str )
    && str.indexOf( firstChar ) === 0
    && str.slice( 1 )
      .split( ',' )
      .every( ( numOrNumRange: string ) => (
        oneDigitRegex.test( numOrNumRange )
        || numRangeRegex.test( numOrNumRange )
      ) )
}

export function validateNumsArr( nums: ValType, maxNum: number, minNum?: number ): boolean {
  const min = isNum( minNum ) ? minNum : 0
  return isArr( nums )
    && nums.every( ( num: number ) => (
      num >= min && num <= maxNum
    ) )
}



export function parseNumOrNumRangesStr( str: ValidStrType, firstChar: FirstCharsType ): ValidValType {
  const nums = []

  str.replace( firstChar, '' )
    .split( ',' )
    .forEach( ( numOrNumRange: string ) => {
      // TODO: add Array.includes polyfill
      if ( numOrNumRange.includes( '-' ) ) {
        nums.push( ...expandNumRange( numOrNumRange ) )
      } else {
        nums.push( parseInt( numOrNumRange ) )
      }
    } )

  nums.sort( sortCb_num )
  return nums
}



export function numsToStrWithCollapsing( nums: number[], firstChar: FirstCharsType ): string {
  nums.sort( sortCb_num )
  const numsOrNumRanges = []
  let sublCount = 0
  nums.forEach( ( num: number, ind: number ) => {
    const nextNum = nums[ ind + 1 ] || 0

    if ( num + 1 === nextNum ) {
      sublCount++
      return
    }

    switch ( true ) {
      case sublCount === 1:
        numsOrNumRanges.push( `${num - 1}`, `${num}` )
        break
      case sublCount > 1:
        numsOrNumRanges.push( `${num - sublCount}-${num}` )
        break
      default:
        numsOrNumRanges.push( `${num}` )
        break
    }
    sublCount = 0

  } )

  return firstChar + numsOrNumRanges.join( ',' )

}


export function isAnyDefault( arr: number[], min: number, max: number ): boolean {
  if ( arr.length === 0 ) return true
  for ( let i = min; i <= max; i++ ) {
    if ( !arr.includes( i ) ) return false
  }
  return true
}

