import { castNum } from '../num'
import { cropStr, clearNonDigits } from '../str'
import type { DataWrapperType, NumCheckerFuncType, ValidateReturnType } from './_types'

export type { DataWrapperType, NumCheckerFuncType, ValidateReturnType }

export default class ValidationUtils {

  static createVal<T>( val: T, opts?: { maxLength?: number } = {} ): DataWrapperType<T> {
    return {
      val,
      isValid: false,
      isChanging: false,
      isEmpty: true,
      maxLength: castNum( opts.maxLength, -1 ),
      get valStr(): string {
        return this.val === -1 ? '' : `${this.val}`
      },
      get shouldDisplayErr(): boolean {
        const { isChanging, isValid, isEmpty } = this
        return !isChanging && !isEmpty && !isValid
      }
    }
  }

  static validateNum(
    arg: { valIn: string, oldVal: number, maxLength: number, checker: NumCheckerFuncType }
  ): ValidateReturnType<number> {

    const val = cropStr( clearNonDigits( arg.valIn ), arg.maxLength )
    const { length } = val
    const isEmpty = length === 0
    return {
      isEmpty,
      isValid: arg.checker( { clearedVal: val, length } ),
      val: isEmpty ? -1 : castNum( val, arg.oldVal )
    }
  }

  static setValidatedNumParam( valWrp: DataWrapperType<number>, params: ValidateReturnType<number> ) {
    valWrp.isEmpty = params.isEmpty
    valWrp.isValid = params.isValid
    valWrp.val = params.val
  }


  static validateAndSetNum(
    valWrp: DataWrapperType<number>,
    valIn: string,
    checker: NumCheckerFuncType
  ) {
    this.setValidatedNumParam( valWrp, ValidationUtils.validateNum( {
      valIn,
      oldVal: valWrp.val,
      maxLength: valWrp.maxLength,
      checker
    } ) )
  }
}

