import {
  isObj, isStr, isF, isNull
} from './is'
import prepareRequestBody from './prepareRequestBody'
import { parse as parseJson } from './json'
import type { MixedMap, StringsMap, HttpMethods as Methods } from './_types'


type MaybeXhr = XMLHttpRequest | null

type RespEventType = 'load' | 'error'

type RespType = {
  status: number,
  response: string,
  type: RespEventType
}

type cbFunc = ( RespType ) => void

type Opts = {
  body?: ?MixedMap,
  url: string,
  sendAs?: 'formData',
  method?: Methods,
  headers?: StringsMap,
  onLoad?: cbFunc,
  onError?: cbFunc,
  onComplete?: cbFunc
}



type LoadJSONreturnType = {
  hasErr: true,
  error: *
} | {
  hasErr: false,
  value: *
}



function isXhr( xhr: MaybeXhr ): boolean %checks {
  return xhr instanceof XMLHttpRequest
}

export default class Xhr {
  url: string

  xhr: MaybeXhr
  method: Methods
  body: null | string | FormData
  query: string
  headers: ?StringsMap
  onLoad: ?cbFunc
  onError: ?cbFunc
  onComplete: ?cbFunc


  constructor( props: Opts ) {
    this.prepareMethod( props.method )
    this.prepareBody( props.body, props.sendAs )
    this.prepareUrl( props.url )
    this.prepareHeaders( props.headers )
    this.onLoad = props.onLoad
    this.onError = props.onError
    this.onComplete = props.onComplete
    this.send()
  }


  static isXhr( maybeXhr: mixed ): boolean {
    return maybeXhr instanceof XMLHttpRequest
  }



  /**
   * Загружает данные из json-файла
   *
   * @static
   * @param {string} filePath
   * @returns {Promise<LoadJSONreturnType>}
   * @memberof Xhr
   * @example
   * Xhr.loadJSON( './path/to/file.json' )
   *   .then( resp => {
   *     console.log( resp )
   *     // { hasErr: false, value: { x: 3 } }
   *     // or
   *     // { hasErr: true, error: 'parse error' | 'loading error' }
   *   } )
   */
  static loadJSON( filePath: string ): Promise<LoadJSONreturnType> {
    return new Promise( ( resolve: * ) => {
      const xhr = new XMLHttpRequest()
      function cb( err: string | null, value: *, responseText?: string ) {
        xhr.onload = () => {}
        xhr.onerror = () => {}
        if ( !isNull( err ) ) {
          resolve( { hasErr: true, error: err, responseText } )
        } else {
          resolve( { hasErr: false, value, responseText } )
        }
      }
      xhr.overrideMimeType( 'application/json' )
      xhr.open( 'GET', filePath, true )
      xhr.onload = function () {
        if ( xhr.status === 200 ) {
          const resultJson = parseJson( xhr.responseText )
          if ( resultJson !== null ) {
            cb( null, resultJson, xhr.responseText )
          } else {
            cb( 'parse error', null, xhr.responseText )
          }
        } else {
          cb( 'loading error' )
        }
      }
      xhr.onerror = () => {
        cb( 'loading error' )
      }
      xhr.send( null )

    } )
  }



  prepareMethod( method: ?Methods ) {
    this.method = isStr( method ) ? method : 'GET'
  }



  prepareBody( body: ?MixedMap, sendAs: ?'formData' ) {
    const { method } = this

    if ( method === 'GET' ) {
      const query = prepareRequestBody( { body, as: 'query' } )
      this.query = isStr( query ) ? query : ''
      this.body = null
      return
    }

    this.query = ''

    const asFormData = sendAs === 'formData'

    this.body = prepareRequestBody( { body, as: asFormData ? 'formData' : 'json' } )
  }



  prepareUrl( url: string ) {
    this.url = url + this.query
  }



  prepareHeaders( headers: ?StringsMap ) {
    this.headers = isObj( headers ) ? headers : null
  }


  send() {
    const {
      url,
      method,
      body,
      headers
    } = this

    const xhr = new XMLHttpRequest()
    xhr.open( method, url )

    if ( isObj( headers ) ) {
      Object.keys( headers ).forEach( ( headerKey: string ) => {
        const headerVal = headers[ headerKey ]
        xhr.setRequestHeader( headerKey, headerVal )
      } )
    }

    xhr.onload = this._onLoad.bind( this )
    xhr.onerror = this._onError.bind( this )

    xhr.send( body )

    this.xhr = xhr
  }



  static getResp( trg: EventTarget, respEventType: RespEventType ): RespType {

    const defs = {
      type: respEventType,
      response: '',
      status: -1
    }

    if ( !( trg instanceof XMLHttpRequest ) ) return defs

    return {
      type: respEventType,
      response: trg.responseText,
      status: trg.status
    }
  }


  _onLoad( ev: ProgressEvent ) {
    const resp = Xhr.getResp( ev.target, 'load' )
    if ( isF( this.onLoad ) ) {
      const cb: cbFunc = this.onLoad
      cb( resp )
    }
    this._onComplete( resp )
  }



  _onError( ev: ProgressEvent ) {
    const resp = Xhr.getResp( ev.target, 'error' )
    if ( isF( this.onError ) ) {
      const cb: cbFunc = this.onError
      cb( resp )
    }
    this._onComplete( resp )
  }


  _onComplete( resp: RespType ) {
    if ( isF( this.onComplete ) ) {
      const cb: cbFunc = this.onComplete
      cb( resp )
    }
    this.destroy()
  }




  destroy() {
    const { xhr } = this

    if ( isXhr( xhr ) ) {
      xhr.onload = function () {}
      xhr.onerror = function () {}
    }
    this.xhr = null

    this.onLoad = null
    this.onError = null
    this.onComplete = null
    this.headers = null
    this.body = null

  }
}

