import EventEmitter from '../EventEmitter'

class Sample extends EventEmitter {
  destroy() {
    this.off()
  }
}

const sample = new Sample()

describe( 'EventEmitter', () => {
  test( 'подписка и вызов', () => {
    const cb = jest.fn()
    sample.on( 'update', () => {
      cb()
    } )
    sample.on( 'update', () => {
      cb()
    } )
    sample.on( 'change', () => {
      cb()
    } )
    sample.emit( 'update' )
    sample.emit( 'change' )

    expect( cb ).toHaveBeenCalledTimes( 3 )
  } )

  test( 'передаваемые аргументы', () => {
    const obj = { x: 3 }
    const cb = jest.fn()
    sample.on( 'get', cb )
    sample.emit( 'get', obj )
    expect( cb ).toHaveBeenCalledWith( obj )
  } )

  test( 'отписка', () => {
    const testCB = jest.fn()
    const cb = () => {
      testCB()
    }
    sample.on( 'set', cb )
    sample.emit( 'set' )
    sample.off( 'set', cb )
    sample.emit( 'set' )
    expect( testCB ).toHaveBeenCalledTimes( 1 )
    sample.off()
    expect( sample._listeners ).toEqual( {} )
  } )

} )

