export type MixedMap = { [ string ]: mixed }
export type AnyMap = { [ string ]: any }
export type StringsMap = { [ string ]: string }
export type InferenceMap = { [ string ]: * }

export type HttpMethods = 'GET' | 'POST' | 'PUT' | 'DELETE'

export type RespErrType = {|
  hasErr: true,
  error: AnyMap,
  status: number,
  respText: string
|}

export type RespSuccessType = {|
  hasErr: false,
  data: AnyMap,
  status: number,
