// @flow

import { castObj } from '../obj'
import { castNum } from '../num'
import {
  isBool, isUndef, isStr
} from '../is'


type ParamsType = {|
  inData?: boolean
|}



/**
 * Поисковик, удалятор, вставлятор, переключатор
 *
 * @class Finder
 * @template T
 */
class Finder<T> {

  static _IN_DATA_DEFAULT: boolean = true

  _isCleared: boolean = false

  _arr: Array<T>
  _inData: boolean
  _finded: T | null = null
  _findedIndex: number = -1




  /**
   * Создаёт экземляр Finder
   * @param {Array<T>} arr массив, с которым будем работать
   * @param {ParamsType} [paramsIn]
   * @param {boolean} [paramsIn.inData] брать id и type из item.data. если не указано - true
   * @memberof Finder
   */
  constructor( arr: Array<T>, paramsIn?: ParamsType ) {
    this._arr = arr
    const params = castObj( paramsIn )
    this._inData = isBool( params.inData )
      ? params.inData
      : Finder._IN_DATA_DEFAULT
  }


  /**
   * Создаёт экземпляр Finder,
   * аналогично new Finder( arr, ?params )
   *
   * @static
   * @param {Array<T>} arr
   * @param {ParamsType} [params]
   * @returns {Finder<T>}
   * @memberof Finder
   */
  static from( arr: Array<T>, params?: ParamsType ): Finder<T> {
    return new Finder( arr, params )
  }


  static _createIdGetter( inData: boolean ) {
    if ( !inData ) {
      return function ( item: any ) { return item.id }
    }
    return function ( item: any ) { return item.data.id }
  }

  static _createTypeGetter( inData: boolean ) {
    if ( !inData ) {
      return function ( item: any ) { return item.type }
    }
    return function ( item: any ) { return item.data.type }
  }



  /**
   * Возвращает найдённой элемент или null
   * (чистит за собой)
   *
   * @returns {(T | null)}
   * @memberof Finder
   */
  value(): T | null {
    const f = this._finded
    this.clear()
    return f
  }



  /**
   * Возвращает индекс найденного элемента или -1
   * (чистит за собой)
   *
   * @returns {number}
   * @memberof Finder
   */
  valueInd(): number {
    const ind = castNum( this._findedIndex, -1 )
    this.clear()
    return ind
  }




  /**
   * Ищет по id
   *
   * @param {string} id
   * @returns {Finder<T>}
   * @memberof Finder
   */
  byId( id: string ): Finder<T> {
    if ( this._isCleared ) return this
    const inData = this._inData

    const idGetter = Finder._createIdGetter( inData )
    const item = this._arr.find( ( it: T, ind: number ): boolean => {
      // $FlowFixMe
      if ( idGetter( it ) === id ) {
        this._findedIndex = ind
        return true
      }
      return false
    } )
    this._setFinded( item )
    return this
  }


  byIdAndType( id: string, type: * ): Finder<T> {
    if ( this._isCleared ) return this
    const inData = this._inData

    const idGetter = Finder._createIdGetter( inData )
    const typeGetter = Finder._createTypeGetter( inData )

    const item = this._arr.find( ( it: T, ind: number ): boolean => {

      // $FlowFixMe
      if ( idGetter( it ) === id && typeGetter( it ) === type ) {
        this._findedIndex = ind
        return true
      }
      return false
    } )
    this._setFinded( item )
    return this
  }



  byItem( item: T ): Finder<T> {
    if ( this._isCleared ) return this

    const id = Finder._createIdGetter( this._inData )( item )
    const type = Finder._createTypeGetter( this._inData )( item )
    if ( isStr( type ) ) {
      this.byIdAndType( id, type )
    } else {
      this.byId( id )
    }

    return this
  }



  del() {
    if ( this._isCleared ) return

    const findedIndex = this._findedIndex

    if ( findedIndex !== -1 ) {
      this._arr.splice( findedIndex, 1 )
    }

    this.clear()
  }



  pushIfNotExist( item: T ) {
    if ( this._isCleared ) return
    this.byItem( item )
    if ( this._findedIndex === -1 ) {
      this._arr.push( item )
    }
    this.clear()
  }




  toggle( item: T, force?: boolean ) {
    if ( this._isCleared ) return
    this.byItem( item )

    const isIn = this._findedIndex !== -1

    if ( force === true && !isIn ) {
      this._arr.push( item )
    } else if ( force === false && isIn ) {
      this.del()
    } else if ( isIn ) {
      this.del()
    } else {
      this._arr.push( item )
    }

    this.clear()
  }




  _setFinded( item: T | void ) {
    this._finded = isUndef( item ) ? null : item
  }


  clear() {
    if ( this._isCleared ) return
    delete this._arr
    this._finded = null
    this._findedIndex = -1
    this._isCleared = true
  }

}


export default Finder

// @flow

import { isArr, isUndef, isObj } from '../is'


type HasIdItemType = { id: string }
type HasIdTypeItemType = { id: string, type: string }



export { default as Finder } from './Finder'




/**
 * Возвращает переданный аргумент, если он является массивом, иначе новый пустой массив
 *
 * @export
 * @param {?Array<*>} maybeArr
 * @example
 * castArr( [ 1, 2, 3 ] ) // [ 1, 2, 3 ]
 * castArr( { x: [ 1, 2, 3 ] } ) // []
 * @returns {Array<*>}
 */
export function castArr<T>( maybeArr: ?Array<T> ): Array<T> {
  if ( isArr( maybeArr ) ) {
    return maybeArr
  }

  return []
}



/**
 * Возвращает новый массив и исключает из него элемент,
 * если он присутствует, либо добавляет, если его там нет
 *
 * @export
 * @template T
 * @param {Array<T>} array
 * @param {T} item
 * @example
 * toggleIncluding( [ 1, 2, 3 ], 2 ) // [ 1, 3 ]
 * toggleIncluding( [ 1, 2, 3 ], 5 ) // [ 1, 2, 3, 5 ]
 * @returns {Array<T>}
 */
export function toggleIncluding<T>( array: Array<T>, item: T ): Array<T> {
  const arr = array.slice( 0 )
  const ind = arr.indexOf( item )
  if ( ind !== -1 ) {
    arr.splice( ind, 1 )
  } else {
    arr.push( item )
  }
  return arr
}



/**
 * Заменяет элементы массива если они переданы вторым аргументом,
 * иначе очищает массив
 *
 * @export
 * @template T
 * @param {Array<T>} arr
 * @param {Array<T>} [newVals]
 */
export function resetArr<T>( arr: Array<T>, newVals?: Array<T> ) {
  arr.splice( 0, arr.length, ...castArr( newVals ) )
}



/**
 * Поиск индекса элемента в массиве по id
 *
 * @export
 * @template T
 * @template HasIdItemType
 * @param {Array<T>} arr
 * @param {string} id
 * @returns {(T | null)}
 */
export function findById<T: HasIdItemType>( arr: Array<T>, id: string ): T | null {
  const item = arr.find( ( it: T ): boolean => it.id === id )
  return isUndef( item ) ? null : item
}



export function findByIdInData<T>( arr: Array<T>, id: string ): T | null {
  const item = arr
    .find( ( it: T ): boolean => isObj( it ) && isObj( it.data ) && it.data.id === id )
  return isUndef( item ) ? null : item
}




/**
 * Поиск индекса элемента в массиве по id
 *
 * @export
 * @template T
 * @template HasIdItemType
 * @param {Array<T>} arr
 * @param {string} id
 * @returns {number}
 */
export function findIndById<T: HasIdItemType>( arr: Array<T>, id: string ): number {
  return arr.findIndex( ( it: T ): boolean => it.id === id )
}



/**
 * Поиск элемента в массиве по id и type
 *
 * @export
 * @template T
 * @template HasIdTypeItemType
 * @param {Array<T>} arr
 * @param {HasIdTypeItemType} params
 * @returns {(T | null)}
 */
export function findByIdAndType<T: HasIdTypeItemType>(
  arr: Array<T>,
  params: HasIdTypeItemType
): T | null {
  const { id, type } = params
  const item = arr.find( ( it: T ): boolean => ( it.id === id ) && ( it.type === type ) )
  return isUndef( item ) ? null : item
}



/**
 * Удаляет элемент из массива, если он там существует
 * (поиск осуществляется по id)
 *
 * @export
 * @template T
 * @param {Array<T>} arr
 * @param {T} item
 * @returns {boolean}
 */
export function findByIdAndDel<T: { id: string }>( arr: Array<T>, id: string ): boolean {
  const ind = findIndById( arr, id )
  if ( ind !== -1 ) {
    arr.splice( ind, 1 )
    return true
  }
  return false
}




/**
 * Добавляет элемент в массив, если его там не существует
 * (поиск осуществляется по id)
 *
 * @export
 * @template T
 * @template HasIdItemType
 * @param {Array<T>} arr
 * @param {T} item
 * @returns {boolean}
 */
export function pushIfNotExist<T: HasIdItemType>(
  arr: Array<T>,
  item: T
): boolean {

  const hasItem = findIndById( arr, item.id ) !== -1
  if ( !hasItem ) {
    arr.push( item )
    return true
  }
  return false
}

