import { isF } from './is'

type ArgsType = any[]


/**
 * Вернёт версию функции, исполнение которой начнётся не ранее,
 * чем истечёт промежуток wait, после её последнего вызова
 *
 * @export
 * @param {function} func
 * @param {number} ms
 * @example
 * const lazyOnresizeCallback = _.debounce(calculateLayout, 300);
 * window.onresize = lazyOnresizeCallback
 * @returns {function}
 */
export function debounce( func: ( ...args: ArgsType ) => void, ms: number ): * {
  let timer = null

  const clearTimer = () => {
    if ( timer !== null ) {
      clearTimeout( timer )
      timer = null
    }
  }

  return ( ...args: ArgsType ) => {
    const onComplete = () => {
      func( ...args )
      clearTimer()
    }

    clearTimer()

    timer = setTimeout( onComplete, ms )
  }
}



/**
 * Вернёт версию функции, которая будет вызываться не чаще чем ms
 *
 * @export
 * @param {function} func
 * @param {number} ms
 * @example
 * const lazyOnresizeCallback = _.throttle(calculateLayout, 300);
 * window.onresize = lazyOnresizeCallback
 * @returns {function}
 */
export function throttle( func: () => void, ms: number ): * {

  let isWaiting = false
  let savedArgs

  function resultFunc( ...args: ArgsType ) {

    if ( isWaiting ) {
      savedArgs = args
      return
    }

    func( ...args )

    isWaiting = true

    setTimeout( () => {
      isWaiting = false
      if ( savedArgs ) {
        resultFunc( ...savedArgs )
        savedArgs = null
      }
    }, ms )
  }

  return resultFunc
}






/**
 * Функция сортировки чисел от меньшего к большему
 *
 * @export
 * @param {number} a
 * @param {number} b
 * @example
 * [ 3, 7, 1 ].sort( sortCb_num ) // [ 1, 3, 7 ]
 * @returns {number}
 */
export function sortCb_num( a: number, b: number ): number {
  switch ( true ) {
    case a < b:
      return -1
    case a > b:
      return 1
    default:
      return 0
  }
}

/**
 * Функция для сортировки строк по алфавиту
 *
 * @export
 * @param {string} a
 * @param {string} b
 * @example
 * [ 'Z', '1', 'A' ].sort( sortCb_str ) // [ '1', 'A', 'Z' ]
 * @returns {number}
 */
export function sortCb_str( a: string, b: string ): number {
  return a.toLowerCase().localeCompare( b.toLowerCase() )
}


/**
 * Функция для сортировки массива объектов по свойству name по алфавиту
 *
 * @export
 * @param {{ name: string }} a
 * @param {{ name: string }} b
 * @example
 * [ { name: 'Z' }, { name: 'A' } ].sort( sortCb_collByName )
 * // [ { name: 'A' }, { name: 'Z' } ]
 * @returns {number}
 */
export function sortCb_collByName( a: { name: string }, b: { name: string } ): number {
  return sortCb_str( a.name, b.name )
}


/**
 * Возвращает аргумент, если тот является функцией, иначе пустую функцию
 *
 * @export
 * @param {*} maybeFunc
 * @example
 * const exampleFunc = () => [ 1, 2 ]
 * castFunc( func ) // exampleFunc
 * castFunc( 3 ) // function () {}
 * @returns {function}
 */
export function castFunc( maybeFunc: * ): ( ...args: * ) => * {
  return isF( maybeFunc ) ? maybeFunc : function () {}
}

