/* eslint-disable no-use-before-define */
export default function isEqual( val1: any, val2: any ): boolean {
  const type1 = getFullType( val1 )
  const type2 = getFullType( val2 )
  if ( type1 !== type2 ) return false
  switch ( true ) {
    case isArr( val1 ):
      return isArrEqual( val1, val2 )
    case isObj( val1 ):
      return isObjEqual( val1, val2 )
    case Number.isNaN( val1 ):
      return Number.isNaN( val2 )
    default:
      return val1 === val2
  }
}


function isArrEqual<T>( arr1: Array<T>, arr2: Array<T> ): boolean {
  if ( arr1.length !== arr2.length ) return false

  return !arr1.some( ( val: T, ind: number ) => !isEqual( val, arr2[ ind ] ) )
}


function isObjEqual( obj1: MixedMap, obj2: MixedMap ): boolean {
  const keys1 = Object.keys( obj1 )
  const keys2 = Object.keys( obj2 )
  if ( !isArrEqual( keys1.sort(), keys2.sort() ) ) return false
  return !keys1.some( ( key: string ) => !isEqual( obj1[ key ], obj2[ key ] ) )
}

