export const MAX_SAFE_INTEGER = 9007199254740991

/**
 * Возвращает первый аргумент если это число, иначе второй, если задан, либо - 0
 *
 * @export
 * @param {*} maybeNum
 * @param {number} [def] опционально
 * @example
 * castNum( 3 ) // 3
 * castNum( undefined ) // 0
 * castNum( undefined, 11 ) // 11
 * @returns {number}
 */
export function castNum(
  maybeNum: any,
  def?: number
): number {
  let n = maybeNum
  if ( isStr( n ) ) {
    const hasDorOrComma = n.includes( '.' ) || n.includes( ',' )
    n = hasDorOrComma ? parseFloat( n ) : parseInt( n, 10 )
  }
  if ( isNum( n ) ) return n
  return isNum( def ) ? def : 0
}


/**
 * Возвращает рандомное число в указаннном промежутке, либо если не указан - от 0 до 9007199254740991
 *
 * @export
 * @param {?number} minNum *
 * @param {?number} maxNum *
 * @example
 * randNum() // 123
 * randNum( 999 ) // 1123
 * randNum( 100, 105 ) // 103
 * @returns {number}
 */
export function randNum( minNum: ?number, maxNum: ?number ): number {
  const min = castNum( minNum )
  const max = castNum( maxNum, MAX_SAFE_INTEGER )
  return Math.round( ( max - min ) * Math.random() ) + min
}

