// @flow

import {
  isNum, isObj, isNil, isBlob, isFile, isStr
} from './is'
import type { AnyMap } from './_types'
import { str as jsonStr } from './json'

type BodyInType = null | void | AnyMap
export type AsType = 'json' | 'query' | 'formData' | void

type InputType = { body?: BodyInType, as?: AsType }

export type BodyOutType = null | string | FormData

type FuncType = ( arg: { body: BodyInType, as: AsType } ) => BodyOutType


function toQuery( body: BodyInType ): string {
  if ( !isObj( body ) || Object.keys( body ) === 0 ) return ''

  const b: AnyMap = body
  const pairs = []

  Object.keys( body ).forEach( ( key: string ) => {
    let val = b[ key ]
    val = typeof val === 'object' ? jsonStr( val ) : val
    if ( val !== null ) {
      pairs.push( `${key}=${val}` )
    }
  } )

  return `?${pairs.join( '&' )}`

}



function toFormData( body: BodyInType ): BodyOutType {
  if ( !isObj( body ) ) return null
  const fd = new FormData()

  const b: AnyMap = body

  Object.keys( b ).forEach( ( key: string ) => {
    const val = b[ key ]

    let validVal = null

    if ( isFile( val ) || isBlob( val ) || isStr( val ) ) {
      validVal = val
    } else if ( isNum( val ) ) {
      validVal = `${val}`
    }

    if ( validVal !== null ) {
      fd.append( key, validVal )
    } else {
      console.warn( `value of ${key} is invalid for FormData: ${val}` )
    }
  } )

  return fd
}



function toJSON( body: BodyInType ): BodyOutType {
  if ( isNil( body ) ) return null
  return jsonStr( body )
}

/**
 * Подготавливает body для запросов  в виде json, query-параметров или FormData
 *
 * @param {InputType} [arg]
 * @param {null | void | AnyMap} [arg.body]
 * @param {'json' | 'query' | 'formData' | void} [arg.as] ожидаемый формат, если не указан, то json
 * @example
 * prepareRequestBody( { body: { file: Blob }, as: 'formData' } )
 * // FormData { file: Blob }
 *
 * prepareRequestBody( { body: { x: 3 } } )
 * // "{\"x\":3}"
 *
 * prepareRequestBody( { body: { a: 1, b: 2 }, as: 'query' } )
 * // "?a=1&b=2"
 *
 * prepareRequestBody( { body: undefined } )
 * // null
 * @returns {BodyOutType: null | string | FormData}
 */
const prepareRequestBody: FuncType = ( arg?: InputType ): BodyOutType => {
  const argObj: InputType = isObj( arg ) ? arg : {}
  const { body, as: asType } = argObj
  switch ( asType ) {
    case 'query':
      return toQuery( body )
    case 'formData':
      return toFormData( body )
    default:
      return toJSON( body )
  }
}

export default prepareRequestBody

