// @flow

import { isObj, isStr } from '../is'
import type { StringsMap } from '../_types'

type BooleanMap = { [ string ]: boolean }
export default function (
  cls: BooleanMap,
  classObj?: StringsMap,
  staticClasses?: BooleanMap
): string {

  let clArr = Object.keys( cls ).filter( ( cl: string ) => cls[ cl ] )

  if ( isObj( classObj ) ) {
    const obj: StringsMap = classObj
    clArr = clArr
      .map( ( cl: string ) => obj[ cl ] )
      .filter( ( cl: string ) => isStr( cl ) )
  }

  if ( isObj( staticClasses ) ) {
    const obj: BooleanMap = staticClasses
    Object.keys( obj ).forEach( ( key: string ) => {
      if ( obj[ key ] ) {
        clArr.push( key )
      }
    } )
  }



  return clArr.join( ' ' )
}

// @flow

import { isArr, isObj, isStr } from '../is'


type BooleanMap = { [ string ]: boolean }
type ArgAsArrType = Array<string | BooleanMap>
type ArgType = BooleanMap | ArgAsArrType

function calcFromObj( obj: BooleanMap ): string[] {
  return Object.keys( obj )
    .filter( ( clName: string ): boolean => obj[ clName ] === true )
}

function calcFromArr( arr: ArgAsArrType ): string[] {
  const result = []
  arr.forEach( ( clDefinition: * ) => {
    if ( isStr( clDefinition ) && clDefinition.length !== 0 ) {
      result.push( clDefinition )
    } else if ( isObj( clDefinition ) ) {
      result.push( ...calcFromObj( clDefinition ) )
    }
  } )

  return result
}

const join = ( arr: string[] ) => arr.join( ' ' )



function calcClasses( cls: ArgType ): string {
  if ( isArr( cls ) ) {
    return join( calcFromArr( cls ) )
  } if ( isObj( cls ) ) {
    return join( calcFromObj( cls ) )
  }
  return ''
}


export default calcClasses

