// @flow

import { isStr, isNum } from './is'
import { randNum } from './num'

const spacesRegex = /\s+/g
/**
 * Тримит строку и сокращает число пробелов до одного в середине строки
 *
 * @export
 * @param {string} str "  ab   c  "
 * @returns {string} "ab c"
 */
export function clearWS( str: string ): string {
  return str.replace( spacesRegex, ' ' ).trim()
}

/**
 * Возвращает рандомную строку вида \d+\-random
 *
 * @export
 * @returns {string} "1345234523452-random"
 */
export function randStr(): string {
  return `${randNum()}-random`
}






/**
 * Возвращает переданный аргумент, если он является строкой,
 * либо второй аргумент (если он присутствует и также является строкой ),
 * либо пустую строку
 *
 * @export
 * @param {mixed} maybeStr
 * @param {?string} defaultStr
 * @returns {string}
 */
export function castStr( maybeStr: mixed, defaultStr?: string ): string {
  if ( isStr( maybeStr ) ) return maybeStr
  return isStr( defaultStr ) ? defaultStr : ''
}





/**
 * Обрезает строку до указанной длины если `maxLength` - число и больше нуля
 *
 * @export
 * @param {string} strIn
 * @param {(number | null)} maxLength
 * @returns {string}
 */
export function cropStr( strIn: string, maxLength: number | null ): string {
  let str = castStr( strIn )
  if (
    isNum( maxLength )
    && maxLength > 0
    && str.length > maxLength
  ) {
    str = str.slice( 0, maxLength )
  }
  return str
}


const nonDigitsRegex = /\D+/g
/**
 * Очищает все нечисловые символы
 *
 * @export
 * @param {string} str
 * @returns {string}
 */
export function clearNonDigits( str: string ): string {
  return str.replace( nonDigitsRegex, '' )
}



const nonAlphanumericRegex = /\W+/g
/**
 * Очищает символы не входящие в паттерн [A-Za-z0-9_]
 *
 * @export
 * @param {string} str
 * @returns {string}
 */
export function clearNonAlphanumeric( str: string ): string {
  return str.replace( nonAlphanumericRegex, '' )
}




const nonWordRegex = /\W+|\d+|_+/g
/**
 * Очищает символы не буквенные символы
 *
 * @export
 * @param {string} str
 * @returns {string}
 */
export function clearNonWord( str: string ): string {
  return str.replace( nonWordRegex, '' )
}



const digitsRegex = /\d+/g
/**
 * Очищает все числовые символы
 *
 * @export
 * @param {string} str
 * @returns {string}
 */
export function clearDigits( str: string ): string {
  return str.replace( digitsRegex, '' )
}

/**
 * Переводит первый символ строки в верхний регистр, остальные — в нижний
 *
 * @export
 * @param {string} str
 * @returns {string}
 */
export function capitalizeStr( str: string ): string {
  return str.charAt( 0 ).toUpperCase() + str.slice( 1 ).toLowerCase()
}

