import { isStr, isNum } from './is'

type FlagType = 'g' | 'i' | 'm' | 'u'

const DEFAULT_MAX_LENGTH = 255

const regexps = {
  email: /^[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])$/, // eslint-disable-line max-len
  digit: /\d/,
  capitalLetter: /[A-Z]/,
  smallLetter: /[a-z]/,
  symbol: /[?№~`!@#$%^&*()+=\\/|'"><;:{}[]]/
}

export const hasDigit = ( str: string ): boolean => regexps.digit.test( str )
export const hasSymbol = ( str: string ): boolean => regexps.symbol.test( str )
export const isEmail = ( str: string ): boolean => regexps.email.test( str )
export const isLengthValid = ( arg: { str: string, min?: number, max?: number } ): boolean => {
  const { length } = arg.str
  let { min, max } = arg
  min = isNum( min ) ? min : 0
  max = isNum( max ) ? max : DEFAULT_MAX_LENGTH
  return length >= min && length <= max
}


export function regex( name: $Keys<typeof regexps>, flags?: FlagType ): RegExp {
  const r = regexps[ name ]
  return !isStr( flags ) ? r : new RegExp( r.toString(), flags )
}


